const modelsLookup = {'text-davinci-002': 'text-davinci-002',
'text-chat-davinci-002': 'chatGPTtest',
'code-davinci-002': 'code-davinci-002',
'text-davinci-003': 'test003',
'text-ada-001': 'text-ada-001',
'text-similarity-curie-001': 'text-similarity-curie-001',
'text-similarity-ada-001': 'text-similarity-ada-001'};

export default function getDeplymentName(modelName){
    //modelName is the model names provided by open ai, not the deployment names provided by us.
    if(modelName in modelsLookup ){
        return modelsLookup[modelName];
    }
    throw new Error(`Key "${modelName}" not supported by our Models\n Supported Models can be found at https://ms.portal.azure.com/#@microsoft.onmicrosoft.com/resource/subscriptions/67aa06b0-2686-40dc-92b0-1316ea0304d9/resourceGroups/Shared_AI_Experience/providers/Microsoft.CognitiveServices/accounts/DALLE-Exploration/oaideployments`);
}