import publicGetPromptResponseFromModels from './public-get-prompt-response-from-models.js';
import msftGetPromptResponseFromModels from './msft-get-prompt-response-from-models.js';

console.log('\n\n=======================Public response=========================\n');
var prompt = 'Which religion is very science friendly?';
var model='text-davinci-002';
var publicResponse = await publicGetPromptResponseFromModels(prompt=prompt, model=model);
console.log(publicResponse);

console.log('\n\n=======================Internal response=========================\n');
var msftResponse = await msftGetPromptResponseFromModels(prompt=prompt, model=model);
console.log(msftResponse);
//uncomment below to get only the text Response.
console.log(msftResponse.choices[0].text.trim());